(window.webpackJsonp=window.webpackJsonp||[]).push([[214],{258:function(t,s,o){"use strict";o.r(s);var e=o(2),a=Object(e.a)({},(function(){var t=this,s=t.$createElement,o=t._self._c||s;return o("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[o("h1",{attrs:{id:"唐代·韦庄"}},[o("a",{staticClass:"header-anchor",attrs:{href:"#唐代·韦庄"}},[t._v("#")]),t._v(" 唐代·韦庄")]),t._v(" "),o("blockquote",[o("p",[t._v("韦庄（约836年—约910年），字端己，汉族，长安杜陵（今中国陕西省西安市附近）人，晚唐诗人、词人，五代时前蜀宰相。")])]),t._v(" "),o("blockquote",[o("p",[t._v("文昌右相韦待价七世孙、苏州刺史韦应物四世孙。韦庄出身京兆韦氏东眷逍遥公房。早年屡试不第，直到乾宁元年（894年）年近六十时方考取进士，任校书郎。李询为两川宣瑜和协使时，召韦庄为判官，奉使入蜀，归朝后升任左补阙。")])]),t._v(" "),o("blockquote",[o("p",[t._v("天复元年（901年），韦庄入蜀为王建掌书记，自此终身仕蜀。天祐四年（907年），韦庄劝王建称帝，任左散骑常侍，判中书门下事，定开国制度，举荐张道古等忠直文人。官终吏部侍郎兼平章事（宰相），卒谥“文靖”。")])]),t._v(" "),o("blockquote",[o("p",[t._v("宋代张唐英撰《蜀梼杌》曾高度评价：“不恃权，不行私，惟至公是守，此宰相之任也。”韦庄工诗，与温庭筠同为“花间派”代表作家，并称“温韦”。其诗多以伤时、感旧、离情、怀古为主题；其律诗圆稳整赡、音调浏亮，绝句情致深婉、包蕴丰厚，发人深思；其词多写自身的生活体验和上层社会之冶游享乐生活及离情别绪，善用白描手法，词风清丽。")])]),t._v(" "),o("blockquote",[o("p",[t._v("所著长诗《秦妇吟》反映战乱中妇女的不幸遭遇，在当时颇负盛名，与《孔雀东南飞》、《木兰诗》并称“乐府三绝”。有《浣花集》十卷，后人又辑其词作为《浣花词》。《全唐诗》录其诗三百一十六首。")])]),t._v(" "),o("h1",{attrs:{id:"作品"}},[o("a",{staticClass:"header-anchor",attrs:{href:"#作品"}},[t._v("#")]),t._v(" 作品")]),t._v(" "),o("div",{staticClass:"custom-block tip"},[o("p",{staticClass:"custom-block-title"},[t._v("江外思乡")]),t._v(" "),o("p",[o("strong",[t._v("更被夕阳江岸上，断肠烟柳一丝丝。")])])])])}),[],!1,null,null,null);s.default=a.exports}}]);