(window.webpackJsonp=window.webpackJsonp||[]).push([[279],{344:function(t,s,a){"use strict";a.r(s);var e=a(2),o=Object(e.a)({},(function(){var t=this,s=t.$createElement,a=t._self._c||s;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"宋代·南宋·杨万里"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#宋代·南宋·杨万里"}},[t._v("#")]),t._v(" 宋代·南宋·杨万里")]),t._v(" "),a("blockquote",[a("p",[t._v("杨万里（1127年10月29日—1206年6月15日），字廷秀，号诚斋。汉族江右民系，吉州吉水（今江西省吉水县黄桥镇湴塘村）人。")])]),t._v(" "),a("blockquote",[a("p",[t._v("南宋大臣，著名文学家、爱国诗人，与陆游、尤袤、范成大并称“南宋四大家”（又作“中兴四大诗人”）。因宋光宗曾为其亲书“诚斋”二字，故学者称其为“诚斋先生”。")])]),t._v(" "),a("blockquote",[a("p",[t._v("绍兴二十四年（1154年），杨万里登进士第，历仕宋高宗、孝宗、光宗、宁宗四朝，曾任知奉新县、国子博士、广东提点刑狱、太子侍读、秘书监等职，官至宝谟阁直学士，封庐陵郡开国侯。")])]),t._v(" "),a("blockquote",[a("p",[t._v("开禧二年（1206年），杨万里病逝，年八十。获赠光禄大夫，谥号“文节”。杨万里一生作诗两万多首，传世作品有四千二百首，被誉为一代诗宗。他创造了语言浅近明白、清新自然，富有幽默情趣的“诚斋体”。杨万里的诗歌大多描写自然景物，且以此见长。他也有不少篇章反映民间疾苦、抒发爱国感情的作品。著有《诚斋集》等。")])]),t._v(" "),a("h1",{attrs:{id:"作品"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#作品"}},[t._v("#")]),t._v(" 作品")]),t._v(" "),a("div",{staticClass:"custom-block tip"},[a("p",{staticClass:"custom-block-title"},[t._v("闲居初夏午睡起")]),t._v(" "),a("p",[a("strong",[t._v("日长睡起无情思，闲看儿童捉柳花。")])])])])}),[],!1,null,null,null);s.default=o.exports}}]);